
export interface UserProfile {
  uid: string;
  email: string | null;
  name?: string;
  profileField?: string; // e.g., "Software Engineering", "Product Management"
  role?: string; // e.g., "Frontend Developer", "Senior PM"
  company?: string;
  education?: string;
  phoneNumber?: string;
  
  resumeFileName?: string; // Original name of the uploaded file
  resumeFileUrl?: string; // Download URL from Firebase Storage
  resumeStoragePath?: string; // Full path in Firebase Storage for deletion
  resumeProcessedText?: string; // Client-side extracted text for AI processing

  createdAt: string;
  interviewsTaken?: number;
  isPlusSubscriber?: boolean;
  isAdmin?: boolean; // For admin role
  updatedAt?: string;
}

export interface InterviewSession {
  id: string;
  userId: string;
  duration: 15 | 30 | 45; // minutes
  status: "pending" | "questions_generated" | "started" | "completed" | "cancelled";
  questions?: GeneratedQuestion[]; // Questions generated by AI
  transcript?: string; // Full interview transcript
  feedback?: InterviewFeedback;
  createdAt: string;
  updatedAt?: string;
}

export interface GeneratedQuestion {
  id: string;
  text: string;
  stage: "oral" | "technical_written";
  type: "behavioral" | "technical" | "coding" | "conversational" | "resume_based";
  answer?: string; // User's answer for this question
}

export interface InterviewFeedback {
  overallScore?: number; // 0-100
  overallFeedback: string;
  correctAnswersSummary: string;
  incorrectAnswersSummary: string;
  areasForImprovement: string;
}
